import { useState, useEffect, useContext } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import LoginPage from './LoginPage';
import ContactForm from '../components/messages/ContactForm';
import UsersPage from './UserPage';
import ReportsPage from './ReportsPage';
import Sidebar from '../components/common/Sidebar';
import '../assets/styles/global.css';
import { AuthContext } from '../context/AuthContext';
import AdminPage from './AdminPage';  
import ReaderPage from './ReaderPage';
import './css/HomePage.css';


const HomePage = () => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [token, setToken] = useState(null); 
  const { user, setUser } = useContext(AuthContext);
  const navigate = useNavigate();

  
  useEffect(() => {
    const fetchUser = async () => {
      if (!token) return; 
      try {
        const response = await fetch('http://localhost:5165/api/user/check-login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`, 
          },
        });
        if (response.ok) {
          const result = await response.json();
          setUser(result.user);
        } else {
          setUser(null);
        }
      } catch (error) {
        console.error('Kullanıcı bilgileri alınırken bir hata oluştu:', error);
        setUser(null);
      }
    };

    fetchUser();
  }, [token, setUser]);

  // Fetch countries
  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch('http://localhost:5165/api/countries');
        const result = await response.json();
        setCountries(result.data.countries || []);
      } catch (error) {
        setError('Ülkeler yüklenirken bir hata oluştu.');
        console.error('Ülkeler yüklenirken bir hata oluştu:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCountries();
  }, []);

  // Handle user login
  const handleLogin = async (userData) => {
    try {
      const response = await fetch('http://localhost:5165/api/user/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });
      if (response.ok) {
        const result = await response.json();
        setToken(result.token); 
        setUser(result.user);
        navigate('/');
      } else {
        throw new Error('Giriş yaparken bir hata oluştu.');
      }
    } catch (error) {
      console.error('Giriş yaparken bir hata oluştu:', error);
    }
  };

  // Handle user logout
  const handleLogout = () => {
    setToken(null);
    setUser(null);
    navigate('/');
  };

  if (!user) {
    return (
      <div className="home-page">
        <div className="welcome-container">
        <h1>Contact Form Management System</h1>
      </div>
        <div className="login-contact-container">
          <div className="login-section">
            <h2>Admin Girişi</h2>
            <LoginPage onLogin={(userData) => handleLogin({ ...userData, role: 'admin' })} />
          </div>
          <div className="login-section">
            <h2>Reader Girişi</h2>
            <LoginPage onLogin={(userData) => handleLogin({ ...userData, role: 'reader' })} />
          </div>
          <div className="contact-form-section">
            {loading && <p>Ülkeler yükleniyor...</p>}
            {error && <p>{error}</p>}
            {!loading && !error && <ContactForm countries={countries} />}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="home-page">
      <Sidebar role={user.role} onLogout={handleLogout} />
      <div className="content">
        <Routes>
          {user.role === 'admin' && (
            <>
              <Route path="/messages" element={<ContactForm countries={countries} />} />
              <Route path="/users" element={<UsersPage />} />
              <Route path="/reports" element={<ReportsPage />} />
              <Route path="/admin" element={<AdminPage />} /> {/* Admin Sayfası */}
            </>
          )}
          {user.role === 'reader' && (
            <>
              <Route path="/messages" element={<ContactForm countries={countries} />} />
              <Route path="/reader" element={<ReaderPage />} /> {/* Reader Sayfası */}
            </>
          )}
          <Route path="*" element={<Navigate to="/messages" />} />
        </Routes>
      </div>
    </div>
  );
};

export default HomePage;
