import { useEffect, useState } from "react";
import Sidebar from '../components/common/Sidebar';
import axios from "axios";
import "./css/addUser.css"

export default function AddUser() {
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [userName, setUserName] = useState("");
  const [photo, setPhoto] = useState(null);
  const [fillAllFieldsAlert, setFillAllFieldsAlert] = useState(false);
  const [userAddedAlert, setUserAddedAlert] = useState(false);
  const [userAddedAlertErr, setUserAddedAlertErr] = useState(false);
  const [cardBgColor, setCardBgColor] = useState("rgba(0, 0, 0, 0.5)");


  useEffect(() => {

    setCardBgColor("#999894");

  }, [cardBgColor]);

  const handlePasswordToggle = () => {
    setShowPassword(!showPassword);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value.slice(0, 10));
  };

  const handleUserNameChange = (event) => {
    setUserName(event.target.value);
  };

  const handlePhotoChange = (event) => {
    const selectedPhoto = event.target.files[0];
    setPhoto(selectedPhoto);
  };

  const handleAddUser = () => {
    if (userName && password && photo) {
      const token = localStorage.getItem("token");
      const reader = new FileReader();

      reader.readAsDataURL(photo);
      reader.onloadend = () => {
        const base64Photo = reader.result;

        const newUser = {
          username: userName,
          password: password,
          base64Photo: base64Photo,
        };

        axios
          .post("http://localhost:5165/api/user/add-reader", newUser, {
            headers: { token },
          })
          .then((res) => {
            setUserAddedAlert(true);
            setUserAddedAlertErr(false);
            setFillAllFieldsAlert(false);
            setUserName("");
            setPassword("");
            setPhoto(null);
            console.log("New user successfully added: ", res);


          })
          .catch((err) => {
            setUserAddedAlert(false);
            setUserAddedAlertErr(true);
            setFillAllFieldsAlert(false);

            console.error("User could not be added: ", err);
          });
      };
    } else {
      setFillAllFieldsAlert(true);
      return;
    }
  };

 return (
  <div>
    <Sidebar />
    <div className="container">
      <div className="d-flex justify-content-center align-items-center">
        <div className="card m-5 p-5" style={{ fontSize: "1.5rem", padding: "3rem" }}>
          <div className="card-body">
            <h1
              className="card-title fw-bold text-center text-warning"
              style={{ fontSize: "150px" }} // Increased font size
            >
              ✉️ CFMS
            </h1>
            <h4 style={{ fontSize: "48px" }}>Contact Form Management System</h4> {/* Increased font size */}
            <hr className="mb-5" style={{ borderWidth: "3px" }} /> {/* Thicker line */}
            <form onSubmit={handleLogin}>
              <div className="mb-4">
                <label htmlFor="loginUsername" className="form-label" style={{ fontSize: "24px" }}>
                  Username
                </label>
                <input
                  className="form-control"
                  id="loginUsername"
                  onChange={(e) => setUsername(e.target.value)}
                  style={{ fontSize: "22px", padding: "20px" }} // Increased font size and padding
                />
              </div>
              <div className="mb-4">
                <label htmlFor="loginPassword" className="form-label" style={{ fontSize: "24px" }}>
                  Password
                </label>
                <input
                  type="password"
                  className="form-control"
                  id="loginPassword"
                  onChange={(e) => setPassword(e.target.value)}
                  style={{ fontSize: "22px", padding: "20px" }} // Increased font size and padding
                />
              </div>
              {error && (
                <div className="alert alert-danger d-grid mb-3" style={{ fontSize: "18px" }}>{error}</div> // Increased font size
              )}
              <div className="d-grid">
                <button type="submit" className="btn btn-outline-primary" style={{ fontSize: "20px", padding: "15px" }}>
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
);

}
