import { useEffect, useState } from "react";
import Sidebar from "../components/common/Sidebar";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Modal } from "react-bootstrap";

export default function Messages() {
  const [userRole, setUserRole] = useState("");
  const [messages, setMessages] = useState([]);
  const [outerCardBgColor, setOuterCardBgColor] = useState("rgba(0, 0, 0, 0.2)");
  const [innerCardBgColor, setInnerCardBgColor] = useState("rgba(0, 0, 0, 0.4)");
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const [selectedMessage, setSelectedMessage] = useState(null);

  const handleDelete = (id) => {
    if (userRole === "admin") {
      axios
        .delete(`http://localhost:5165/api/message/${id}`)
        .then((response) => {
          setMessages(messages.filter((message) => message.id !== id));
        })
        .catch((error) => {
          console.error("Error while deleting the message:", error);
        });
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedMessage(null);
  };

  const handleShowModal = (message) => {
    setSelectedMessage(message);
    setShowModal(true);
  };

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!user || !token) {
      navigate("/login");
      return;
    }

    setUserRole(user.role);

    axios
      .get("http://localhost:5165/api/message", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setMessages(response.data.data);
      })
      .catch((error) => {
        console.error("Error while fetching the messages:", error);
      });
  }, [navigate]);

  useEffect(() => {
    setOuterCardBgColor("rgba(0, 0, 0, 0.2)");
    setInnerCardBgColor("rgba(0, 0, 0, 0.4)");
  }, [outerCardBgColor, innerCardBgColor]);

  return (
    <div>
      <Sidebar />
      <div className="container mt-5">
        <div className="card" style={{ backgroundColor: outerCardBgColor }}>
          <div className="card-header">
            <h3 className="card-title text-center text-primary">Messages</h3>
          </div>
          <div className="card-body">
            {messages.length === 0 ? (
              <p>No messages found.</p>
            ) : (
              <table className="table table-hover table-dark table-striped">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Gender</th>
                    <th>Country</th>
                    <th>Message</th>
                    {userRole === "admin" && <th>Action</th>}
                  </tr>
                </thead>
                <tbody>
                  {messages.map((message) => (
                    <tr key={message.id}>
                      <td>{message.name}</td>
                      <td>{message.gender}</td>
                      <td>{message.country}</td>
                      <td>
                        <button
                          className="btn btn-link text-light"
                          onClick={() => handleShowModal(message)}
                        >
                          View Message
                        </button>
                      </td>
                      {userRole === "admin" && (
                        <td>
                          <button
                            className="btn btn-danger"
                            onClick={() => handleDelete(message.id)}
                          >
                            Delete
                          </button>
                        </td>
                      )}
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      </div>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Message Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedMessage && (
            <div
              className="card p-3"
              style={{ backgroundColor: innerCardBgColor }}
            >
              <h5 className="card-title">Name: {selectedMessage.name}</h5>
              <p className="card-text">Gender: {selectedMessage.gender}</p>
              <p className="card-text">Country: {selectedMessage.country}</p>
              <p className="card-text">Message: {selectedMessage.message}</p>
            </div>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
}
