import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

export default function Sidebar() {
  const [userRole, setUserRole] = useState("");
  const [userName, setUserName] = useState("");
  const [userImgBase64, setUserImgBase64] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    setUserRole(JSON.parse(localStorage.getItem("user"))?.role ?? "");
    setUserName(JSON.parse(localStorage.getItem("user"))?.username ?? "");
    setUserImgBase64(
      JSON.parse(localStorage.getItem("user"))?.base64Photo ?? ""
    );

    console.log(userImgBase64);
  }, [userRole, userName, userImgBase64]);

  const handleLogout = () => {
    const token = localStorage.getItem("token");

    axios
      .post("http://localhost:5165/api/user/logout", null, {
        headers: {
          token,
        },
      })
      .then((res) => {
        console.log(res);
        setUserRole("");
        localStorage.removeItem("user");
        navigate("/login");
      })
      .catch((err) => {
        console.error("Could not log out: ", err);
      });
  };

  return (
    <div>
      {(userRole === "admin" || userRole === "reader") && (
        <nav className="Sidebar Sidebar-expand-lg">
          <div className="container-fluid">
            <Link className="Sidebar-brand text-warning fw-bold" to="/">
              🫡 Welcome
            </Link>
            <button
              className="Sidebar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#SidebarNav"
              aria-controls="SidebarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
                >
              <span className="sidebar-toggler-icon"></span>
            </button>
            <div className="collapse sidebar-collapse" id="SidebarNav">
              {userRole === "admin" && (
                <ul className="sidebar-nav me-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to="/messages">
                      Messages
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/users">
                      Users
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/reports">
                      Reports
                    </Link>
                  </li>
                </ul>
              )}
              {userRole === "reader" && (
                <ul className="sidebar-nav me-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to="/messages">
                      Messages
                    </Link>
                  </li>
                </ul>
              )}
              <ul className="Sidebar-nav mb-2 mb-lg-0">
                <li className="nav-item dropdown me-5 dropend">
                  <Link
                    className="nav-link dropdown-toggle"
                    role="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                  >
                    <img
                      src={userImgBase64}
                      alt="Profile Pic"
                      className="rounded-circle img-fluid"
                      style={{ width: "30px", height: "30px" }}
                    />
                  </Link>
                  <ul className="dropdown-menu dropend">
                    <li className="text-center">
                      <span className="dropdown-item-text">{userName}</span>
                    </li>
                    <hr />
                    <li className="dropdown-item d-grid">
                      <button
                        type="button"
                        className="btn btn-outline-danger"
                        onClick={handleLogout}
                      >
                        Logout
                      </button>
                    </li>
                  </ul>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      )}
      {!userRole && (
        <nav className="Sidebar Sidebar-expand-lg">
          <div className="container-fluid">
            <Link className="Sidebar-brand text-warning fw-bold" to="/">
              Home 🏠
            </Link>
            <div className="collapse Sidebar-collapse" id="SidebarNav">
              <button type="button" color="#999894" className="btn btn-outline-primary">
                <Link className="nav-link" to="/login">
                  Login
                </Link>
              </button>
            </div>
            <button
              className="Sidebar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#SidebarNav"
              aria-controls="SidebarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="Sidebar-toggler-icon"></span>
            </button>
            <ul className="Sidebar-nav mb-2 mb-lg-0"></ul>
          </div>
        </nav>
      )}
    </div>
  );
}
