import { useState, useEffect } from 'react';

import LoginPage from './LoginPage';
import ContactForm from '../components/messages/ContactForm';
import '../assets/styles/global.css';

const HomePage = () => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true); // Yüklenme durumu için state ekleyin
  const [error, setError] = useState(null); // Hata durumu için state ekleyin

  const fetchCountries = async () => {
    try {
      const response = await fetch('http://localhost:5165/api/countries');
      if (!response.ok) {
        throw new Error(`HTTP hata kodu: ${response.status}`);
      }
      const data = await response.json();
      console.log('API yanıtı:', data); // Yanıtı kontrol et
      if (Array.isArray(data)) {
        setCountries(data);
      } else {
        throw new Error('Yanıt bir dizi değil');
      }
    } catch (err) {
      console.error('Ülkeler yüklenirken hata oluştu:', err);
      setError(`Ülkeler yüklenirken bir hata oluştu: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };
  
  

  return (
    <div className="home-page">
      <h1>Hoşgeldiniz!</h1>
      <div className="login-contact-container">
        <div className="login-section">
          <LoginPage />
        </div>
        <div className="contact-form-section">
        {loading && <p>Ülkeler yükleniyor...</p>}
          {error && <p>{error}</p>}
          {!loading && !error && <ContactForm countries={countries} />}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
