import { useState, useEffect} from 'react';
import axios from "axios";
import Sidebar from "../components/common/Sidebar";


export default function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [cardBgColor, setCardBgColor] = useState("rgba(0, 0, 0, 0.4)");

  useEffect(() => {
      setCardBgColor("#999894"); 
  }, [cardBgColor]);


  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5165/api/user/login",
        {
          username,
          password,
        }
      );

      if (response.status === 200) {
        const { user, token } = response.data.data;

        localStorage.setItem("user", JSON.stringify(user));
        localStorage.setItem("token", token);

        window.location.href = "/";
        console.log("User: " + localStorage.getItem("user"));
        console.log("Token: " + localStorage.getItem("token"));
      }
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        setError(error.response.data.error);
      } else {
        console.error("Error occurred while logging in:", error);
        
      }
    }
  };

  return (
    <div>
    <Sidebar />
    <div className="container mt-5">
      <div className="card p-5" style={{ fontSize: "1.2rem", padding: "2rem" }}>
        <div className="card-header">
          <h3 style={{ fontSize: "2rem" }}>Add New User</h3> {/* Increased font size */}
        </div>
        <div className="card-body">
          <div className="container">
            <div className="row mb-4">
              <div className="col-3 d-flex align-items-center" style={{ fontSize: "1.2rem" }}> {/* Increased font size */}
                Username:
              </div>
              <div className="col-9">
                <input
                  type="text"
                  id="text"
                  className="form-control"
                  value={userName}
                  onChange={handleUserNameChange}
                  maxLength={10}
                  minLength={1}
                  style={{ fontSize: "1.2rem", padding: "1rem" }} // Increased font size and padding
                />
              </div>
            </div>
            <hr />
            <div className="row mb-4">
              <div className="col-3 d-flex align-items-center" style={{ fontSize: "1.2rem" }}>
                Role:
              </div>
              <div className="col-9" style={{ fontSize: "1.2rem", textTransform: "capitalize" }}>
                Reader
              </div>
            </div>
            <hr />
            <div className="row mb-4">
              <div className="col-3 d-flex align-items-center" style={{ fontSize: "1.2rem" }}>
                Password:
              </div>
              <div className="col-8 d-flex align-items-center">
                <input
                  type={showPassword ? "text" : "password"}
                  id="password"
                  className="form-control"
                  value={password}
                  onChange={handlePasswordChange}
                  maxLength={10}
                  style={{ fontSize: "1.2rem", padding: "1rem", marginRight: '0.5rem' }} // Increased font size and padding
                />
              </div>
              <div className="col d-flex align-items-center justify-content-center clickable-row">
                <span
                  className="material-symbols-outlined"
                  onClick={handlePasswordToggle}
                  style={{ fontSize: "1.5rem" }} // Increased icon size
                >
                  👀
                </span>
              </div>
            </div>
            <hr />
            <div className="row mb-4">
              <div className="col-3 d-flex align-items-center" style={{ fontSize: "1.2rem" }}>
                Profile Picture:
              </div>
              {photo && (
                <div className="col-2">
                  <img
                    src={URL.createObjectURL(photo)}
                    alt="Selected Profile Pic"
                    className="rounded-circle img-fluid"
                    style={{ width: "120px", height: "120px" }} // Increased image size
                  />
                </div>
              )}
              <div className="col d-flex align-items-center">
                <input
                  type="file"
                  id="photo"
                  className="form-control"
                  accept=".jpg, .jpeg, .png"
                  style={{ fontSize: "1.2rem", padding: "1rem" }} // Increased font size and padding
                  onChange={handlePhotoChange}
                />
              </div>
            </div>
            <div className="row mb-4">
              <button
                className="btn btn-outline-primary"
                onClick={handleAddUser}
                style={{
                  borderRadius: '12px',
                  padding: '12px 24px',
                  fontSize: '18px', // Increased font size
                  transition: 'background-color 0.3s ease, border-color 0.3s ease',
                  border: '2px solid #49464a',
                  color: "black",
                }}
                onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#211926'}
                onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'transparent'}
              >
                Add User
              </button>
            </div>
            {fillAllFieldsAlert && (
              <div className="row mb-4">
                <div className="col">
                  <div className="alert alert-primary" style={{ fontSize: "1.2rem" }}> {/* Increased font size */}
                    Please make sure to fill all of the fields
                  </div>
                </div>
              </div>
            )}
            {userAddedAlert && (
              <div className="row mb-4">
                <div className="col">
                  <div className="alert alert-success" style={{ fontSize: "1.2rem" }}> {/* Increased font size */}
                    User added successfully.
                  </div>
                </div>
              </div>
            )}
            {userAddedAlertErr && (
              <div className="row mb-4">
                <div className="col">
                  <div className="alert alert-danger" style={{ fontSize: "1.2rem" }}> {/* Increased font size */}
                    User could not be added.
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
    </div>
  );
}
