import { BrowserRouter as Router, Route, Routes,Navigate, Outlet, useNavigate } from 'react-router-dom';
import { useEffect, useState } from "react";
import { AuthProvider } from './context/AuthContext';
import LoginPage from './pages/LoginPage'; 
import HomePage from './pages/HomePage';
import MessagesPage from './pages/MessagesPage';
import UserPage from './pages/UserPage';
import ReportsPage from './pages/ReportsPage';
import NotAuthorizedPage from './pages/NotAuthorizedPage';
import NotFoundPage from './pages/NotFoundPage';
import { AuthContext } from './context/AuthContext';
import MessageDetails from "./pages/MessageDetails";
import EditUser from "./pages/EditUser";
import AddUser from "./pages/AddUser";

function App() {
  const userRoles = {
    ADMIN: "admin",
    READER: "reader",
  };


  return (
    <BrowserRouter>
      <Routes>
        // Public Pages
        <Route index element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        // Admin and reader pages
        <Route
          path="/messages"
          element={<PrivateRoute roles={[userRoles.ADMIN, userRoles.READER]} />}
        >
          <Route path="/messages" element={<MessagesPage />} />
        </Route>
        <Route
          path="/messages/:messageId"
          element={<PrivateRoute roles={[userRoles.ADMIN, userRoles.READER]} />}
        >
          <Route path="/messages/:messageId" element={<MessageDetails />} />
        </Route>
        // Admin pages
        <Route
          path="/users"
          element={<PrivateRoute roles={[userRoles.ADMIN]} />}
        >
          <Route path="/users" element={<UserPage />} />
        </Route>
        <Route
          path="/users/:id"
          element={<PrivateRoute roles={[userRoles.ADMIN]} />}
        >
          <Route path="/users/:id" element={<EditUser />} />
        </Route>
        <Route
          path="/users/add-new-user"
          element={<PrivateRoute roles={[userRoles.ADMIN]} />}
        >
          <Route path="/users/add-new-user" element={<AddUser />} />
        </Route>
        <Route
          path="/reports"
          element={<PrivateRoute roles={[userRoles.ADMIN]} />}
        >
          <Route path="/reports" element={<ReportsPage />} />
        </Route>
        // Error pages
        <Route path="/*" element={<NoPage />} />
        <Route path="/unauthorized" element={<NotAuthorizedPage />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
