import { useState, useEffect } from 'react';
import axios from "axios";
import Sidebar from '../components/common/Sidebar';
import { useDarkMode } from "../context/DarkModeContext";

export default function HomePage() {
  const [formData, setFormData] = useState({
    name: "",
    message: "",
    gender: "male",
    country: "",
  });

  const [countries, setCountries] = useState([]);
  const [error, setError] = useState(null);
  const [showSuccessAlert, setShowSuccessAlert] = useState(false);
  const [cardBgColor, setCardBgColor] = useState("rgba(0, 0, 0, 0.4)");
  const { darkMode } = useDarkMode();

  useEffect(() => {
      setCardBgColor("#999894"); 
  }, [cardBgColor]);

  useEffect(() => {
    axios
      .get("http://localhost:5165/api/countries")
      .then((res) => {
        setCountries(res.data.data.countries);
      })
      .catch((err) => {
        console.error("Error while fetching the countries", err);
      });
  }, []);

  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };


  // Handle user login
  const handleLogin = (e) => {
    e.preventDefault();
    console.log("Form is being submitted:", formData);

    axios
      .post("http://localhost:5165/api/message/add", formData)
      .then((res) => {
        console.log("Form submitted succesffully:", res.data);
        setShowSuccessAlert(true);
      })
      .catch((err) => {
        console.error("Error sending form data:", err);
      setError(true);
      });
  };



  return (
    <div className={darkMode ? "bg-dark text-light" : ""}>
    <div>
      <Sidebar />
      <div className="container mt-5 d-flex justify-content-center align-items-center">
        <div className="card w-50" style={{ backgroundColor: cardBgColor }}>
          <div className="card-header">
            <h2 className="card-title">
              {"Contact Us"}
            </h2>
          </div>
          <div className="card-body">
            {showSuccessAlert && (
              <div
                className="alert alert-primary alert-dismissible fade show mt-3 mb-3"
                role="alert"
              >
                {"Form submitted successfully."}
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="alert"
                  aria-label="Close"
                  onClick={() => setShowSuccessAlert(false)}
                ></button>
              </div>
            )}

            {error && (
              <div
                className="alert alert-danger alert-dismissible fade show mt-3 mb-3"
                role="alert"
              >
                { "An error occurred while submitting the form."}
                <button
                  type="button"
                  className="btn-close"
                  data-bs-dismiss="alert"
                  aria-label="Close"
                  onClick={() => setError(false)}
                ></button>
              </div>
            )}

            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label htmlFor="name" className="form-label">
                  { "Full Name" }
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  maxLength="50"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">
                  { "Gender"}
                </label>
                <div className="form-check">
                  <input
                    type="radio"
                    className="form-check-input"
                    id="male"
                    name="gender"
                    value="male"
                    checked={formData.gender === "male"}
                    onChange={handleInputChange}
                  />
                  <label className="form-check-label" htmlFor="male">
                    {"Male"}
                  </label>
                </div>
                <div className="form-check">
                  <input
                    type="radio"
                    className="form-check-input"
                    id="female"
                    name="gender"
                    value="female"
                    checked={formData.gender === "female"}
                    onChange={handleInputChange}
                  />
                  <label className="form-check-label" htmlFor="female">
                    {"Female"}
                  </label>
                </div>
              </div>
              <div className="mb-3">
                <label htmlFor="country" className="form-label">
                  {"Country" }
                </label>
                <select
                  className="form-select"
                  id="country"
                  name="country"
                  value={formData.country}
                  onChange={handleInputChange}
                  required
                >
                  <option value="" disabled>
                    {"Select a country"}
                  </option>
                  {countries.map((country, index) => (
                    <option key={index} value={country}>
                      {country}
                    </option>
                  ))}
                </select>
              </div>
              <div className="mb-3">
                <label htmlFor="message" className="form-label">
                  Message
                </label>
                <textarea
                  className="form-control"
                  id="message"
                  name="message"
                  rows="5"
                  maxLength="500"
                  value={formData.message}
                  onChange={handleInputChange}
                  required
                ></textarea>
              </div>
              <div className="d-grid">
              <button
  type="submit"
  className="btn"
  style={{
    backgroundColor: "#6c757d", 
    color: "white", 
    borderColor: "#6c757d", 
    borderRadius: "4px", 
    padding: "10px 20px", 
    fontSize: "16px" 
  }}
  onMouseOver={(e) => {
    e.currentTarget.style.backgroundColor = "#5a6268"; 
    e.currentTarget.style.borderColor = "#545b62";
  }}
  onMouseOut={(e) => {
    e.currentTarget.style.backgroundColor = "#6c757d"; 
    e.currentTarget.style.borderColor = "#6c757d"; 
  }}
>
  Submit
</button>

              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
}
