// src/context/AuthContext.jsx
import { createContext, useState, useEffect } from 'react';
import PropTypes from 'prop-types'; // prop-types'i import et
import { fetchWithAuth } from '../utils/api'; // Token ile API çağrısı için

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const checkLogin = async () => {
      try {
        const response = await fetchWithAuth('/api/user/check-login');
        setUser(response); // Kullanıcı bilgilerini al
      } catch (err) {
        setUser(null);
      }
    };

    checkLogin();
  }, []);

  return (
    <AuthContext.Provider value={{ user }}>
      {children}
    </AuthContext.Provider>
  );
};

// PropTypes ile children'ı doğrula
AuthProvider.propTypes = {
  children: PropTypes.node.isRequired,
};
