import { useState } from 'react';
import axios from "axios";
import Sidebar from "../components/common/Sidebar";

export default function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5165/api/user/login",
        {
          username,
          password,
        }
      );

      if (response.status === 200) {
        const { user, token } = response.data.data;

        localStorage.setItem("user", JSON.stringify(user));
        localStorage.setItem("token", token);

        window.location.href = "/";
        console.log("User: " + localStorage.getItem("user"));
        console.log("Token: " + localStorage.getItem("token"));
      }
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        setError(error.response.data.error);
      } else {
        console.error("Error occurred while logging in:", error);
      }
    }
  };

  const containerStyle = {
    marginTop: '50px',
    backgroundColor: '#000', /* Sayfan覺n arka plan rengini siyah yapar */
    color: '#fff', /* Yaz覺 rengini beyaz yapar */
  };

  const cardStyle = {
    backgroundColor: '#333', /* Kart覺n arka plan rengini koyu gri yapar */
    border: 'none', /* Kart kenarl覺覺n覺 kald覺r覺r */
    borderRadius: '10px', /* Kart k繹elerini yuvarlat覺r */
  };

  const formControlStyle = {
    backgroundColor: '#222', /* Giri alan覺 arka plan rengini koyu gri yapar */
    color: '#fff', /* Giri alan覺 yaz覺 rengini beyaz yapar */
    border: '1px solid #444', /* Giri alan覺 kenarl覺k rengini koyu gri yapar */
    borderRadius: '5px', /* Giri alan覺 k繹elerini yuvarlat覺r */
  };

  const placeholderStyle = {
    color: '#aaa', /* Placeholder metin rengini gri yapar */
  };

  const btnLoginStyle = {
    backgroundColor: '#e94057', /* Buton arka plan rengini pembe yapar */
    color: '#fff', /* Buton 羹zerindeki metin rengini beyaz yapar */
    border: 'none', /* Kenarl覺k yok */
    borderRadius: '5px', /* K繹eleri yuvarlatma */
    padding: '10px 20px', /* 襤癟 boluk */
    fontSize: '16px', /* Yaz覺 tipi boyutu */
    fontWeight: 'bold', /* Yaz覺 tipi kal覺nl覺覺 */
    cursor: 'pointer', /* Fare imlecini pointer yapar */
    transition: 'background-color 0.3s ease', /* Arka plan rengi ge癟i efekti */
  };

  const btnLoginHoverStyle = {
    backgroundColor: '#f27121', /* zerine gelindiinde arka plan rengini deitirir */
  };

  return (
    <div>
      <Sidebar />
      <div style={containerStyle} className="container">
        <div className="d-flex justify-content-center align-items-center">
          <div style={cardStyle} className="card m-5 p-5">
            <div className="card-body">
              <h1 className="card-title fw-bold text-center text-warning text-strong" style={{ fontSize: "80px" }}>
                哄 CFMS
              </h1>
              <h4>Contact Form Management Systems</h4>
              <hr className="mb-5" />
              <form onSubmit={handleLogin}>
                <div className="mb-3">
                  <label htmlFor="loginUsername" className="form-label" style={{ color: '#e94057' }}>
                    Username
                  </label>
                  <input
                    className="form-control"
                    id="loginUsername"
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Enter username"
                    style={formControlStyle}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="loginPassword" className="form-label" style={{ color: '#e94057' }}>
                    Password
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="loginPassword"
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter password"
                    style={formControlStyle}
                  />
                </div>
                {error && (
                  <div className="alert alert-danger d-grid mb-2" style={{ backgroundColor: '#f44336', color: '#fff', borderRadius: '5px' }}>
                    {error}
                  </div>
                )}
                <div className="d-grid">
                  <button
                    type="submit"
                    style={btnLoginStyle}
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = btnLoginHoverStyle.backgroundColor}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = btnLoginStyle.backgroundColor}
                  >
                    Login
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
