import { useState, useEffect } from 'react';
import LoginPage from './LoginPage';
import ContactForm from '../components/messages/ContactForm';
import '../assets/styles/global.css';

const HomePage = () => {
  const [user, setUser] = useState(null); // Kullanıcı bilgilerini yönetmek için useState
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        // Kullanıcı bilgilerini al
        const response = await fetch('http://localhost:5165/api/user');
        const result = await response.json();
        setUser(result.user);
      } catch (error) {
        console.error('Kullanıcı bilgileri alınırken bir hata oluştu:', error);
      }
    };

    fetchUser();
  }, []);

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch('http://localhost:5165/api/countries');
        const result = await response.json();
        setCountries(result.data.countries || []);
      } catch (error) {
        setError('Ülkeler yüklenirken bir hata oluştu.');
        console.error('Ülkeler yüklenirken bir hata oluştu:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCountries();
  }, []);

  if (!user) {
    return <LoginPage />;
  }

  return (
    <div className="home-page">
      <h1>Hoşgeldiniz!</h1>
      <div className="login-contact-container">
        <div className="contact-form-section">
          {loading && <p>Ülkeler yükleniyor...</p>}
          {error && <p>{error}</p>}
          {!loading && !error && <ContactForm countries={countries} />}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
