import { useState, useEffect} from 'react';
import axios from "axios";
import Sidebar from "../components/common/Sidebar";


export default function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [cardBgColor, setCardBgColor] = useState("rgba(0, 0, 0, 0.4)");

  useEffect(() => {
      setCardBgColor("#999894"); 
  }, [cardBgColor]);


  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5165/api/user/login",
        {
          username,
          password,
        }
      );

      if (response.status === 200) {
        const { user, token } = response.data.data;

        localStorage.setItem("user", JSON.stringify(user));
        localStorage.setItem("token", token);

        window.location.href = "/";
        console.log("User: " + localStorage.getItem("user"));
        console.log("Token: " + localStorage.getItem("token"));
      }
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        setError(error.response.data.error);
      } else {
        console.error("Error occurred while logging in:", error);
        
      }
    }
  };

  return (
    <div>
      <Sidebar />
      <div className="container">
        <div className="d-flex justify-content-center align-items-center">
          <div className="card m-5 p-5" style={{ fontSize: "1.5rem", padding: "3rem" }}>
            <div className="card-body">
              <h1
                className="card-title fw-bold text-center text-warning text-strong"
                style={{ fontSize: "150px" }} // Increased font size
              >
                ✉️ CFMS
              </h1>
              <h4 style={{ fontSize: "48px" }}>Contact Form Management Systems</h4> {/* Increased font size */}
              <hr className="mb-5" style={{ borderWidth: "3px" }} /> {/* Thicker line */}
              <form onSubmit={handleLogin}>
                <div className="mb-4">
                  <label htmlFor="loginUsername" className="form-label" style={{ fontSize: "24px" }}>
                    Username
                  </label>
                  <input
                    className="form-control"
                    id="loginUsername"
                    onChange={(e) => setUsername(e.target.value)}
                    style={{ fontSize: "22px", padding: "20px" }} // Increased font size and padding
                  />
                </div>
                <div className="mb-4">
                  <label htmlFor="loginPassword" className="form-label" style={{ fontSize: "24px" }}>
                    Password
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="loginPassword"
                    onChange={(e) => setPassword(e.target.value)}
                    style={{ fontSize: "22px", padding: "20px" }} // Increased font size and padding
                  />
                </div>
                {error && (
                  <div className="alert alert-danger d-grid mb-3" style={{ fontSize: "18px" }}>{error}</div> // Increased font size
                )}
                <div className="d-grid">
                  <button type="submit" className="btn btn-outline-primary" style={{ fontSize: "20px", padding: "15px" }}>
                    Login
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
