import { useEffect, useRef } from "react";
import PropTypes from "prop-types";
import Chart from "chart.js/auto";

function PieChart({ chartId, maleCount, femaleCount }) {
  const canvasRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    const context = canvasRef.current.getContext("2d");

    if (chartInstance.current) {//mevcutsa
      chartInstance.current.data.datasets[0].data = [maleCount, femaleCount];
      chartInstance.current.update();
    } else {
      chartInstance.current = new Chart(context, {
        type: "pie",
        data: {
          labels: ["Male", "Female"],
          datasets: [
            {
              label: "Gender Distribution",
              data: [maleCount, femaleCount],
              backgroundColor: ["#007bff", "#ff007f"],
              borderColor: "#ffffff",
              borderWidth: 2,
            },
          ],
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            title: {
              display: false,
              font: {
                size: 28,
                weight: "normal",
              },
              text: "Gender Distribution",
              color: "white",
            },
            legend: {
              labels: {
                color: "white",
              },
            },
          },
        },
      });
    }

    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
        chartInstance.current = null;
      }
    };
  }, [chartId, maleCount, femaleCount]);

  return <canvas ref={canvasRef} id={`pieChart-${chartId}`} />;
}

PieChart.propTypes = {
  chartId: PropTypes.string.isRequired,
  maleCount: PropTypes.number.isRequired,
  femaleCount: PropTypes.number.isRequired,
};

export default PieChart;
