import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

export default function Sidebar() {
  const [userRole, setUserRole] = useState("");
  const [userName, setUserName] = useState("");
  const [userImgBase64, setUserImgBase64] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    setUserRole(JSON.parse(localStorage.getItem("user"))?.role ?? "");
    setUserName(JSON.parse(localStorage.getItem("user"))?.username ?? "");
    setUserImgBase64(
      JSON.parse(localStorage.getItem("user"))?.base64Photo ?? ""
    );
  }, [userRole, userName, userImgBase64]);

  const handleLogout = () => {
    const token = localStorage.getItem("token");

    axios
      .post("http://localhost:5165/api/user/logout", null, {
        headers: {
          token,
        },
      })
      .then((res) => {
        setUserRole("");
        localStorage.removeItem("user");
        navigate("/login");
      })
      .catch((err) => {
        console.error("Could not log out: ", err);
      });
  };

  return (
    <div>
      {(userRole === "admin" || userRole === "reader") && (
        <nav className="Sidebar Sidebar-expand-lg">
          <div className="container-fluid">
            <Link className="Sidebar-brand text-warning fw-bold" to="/">
              ü´° Welcome
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#SidebarNav"
              aria-controls="SidebarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse" id="SidebarNav">
              {userRole === "admin" && (
                <ul className="sidebar-nav me-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to="/messages">
                      <h7 style={{ color: "#bbc1c9" }}>{"Messages"}</h7>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/users">
                      <h7 style={{ color: "#bbc1c9" }}>{"Users"}</h7>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/reports">
                      <h7 style={{ color: "#bbc1c9" }}>{"Reports"}</h7>
                    </Link>
                  </li>
                </ul>
              )}
              {userRole === "reader" && (
                <ul className="navbar-nav me-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to="/messages" style={{ color: "white" }}>
                      <h7 style={{ color: "black" }}>{"Messages"}</h7>
                    </Link>
                  </li>
                </ul>
              )}
              <ul className="Sidebar-nav mb-2 mb-lg-0">
                <li className="nav-item dropdown me-5">
                  <Link
                    className="nav-link dropdown-toggle"
                    role="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                  >
                    <img
                      src={userImgBase64}
                      alt="Profile Pic"
                      className="rounded-circle img-fluid"
                      style={{ width: "30px", height: "30px" }}
                    />
                  </Link>
                  <ul className="dropdown-menu">
                    <li className="text-center">
                      <span className="dropdown-item-text">{userName}</span>
                    </li>
                    <hr />
                    <li className="dropdown-item d-grid">
                      <button
                        type="button"
                        className="btn custom-logout-button"
                        style={{ backgroundColor: "#85898f", color: "#2f3133", border: "black" }}
                        onClick={handleLogout}
                      >
                        Logout
                      </button>
                    </li>
                  </ul>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      )}
      {!userRole && (
        <nav className="Sidebar Sidebar-expand-lg">
          <div className="container-fluid">
            <Link className="Sidebar-brand text-warning fw-bold" to="/">
              Home üè†
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#SidebarNav"
              aria-controls="SidebarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse" id="SidebarNav">
              <button type="button" className="btn custom-logout-button"
                style={{ backgroundColor: "#85898f", color: "#2f3133", border: "black" }}
              >
                <Link className="nav-link" to="/login">
                  Login
                </Link>
              </button>
            </div>
          </div>
        </nav>
      )}
    </div>
  );
}
