import { useState, useEffect } from 'react';
import { fetchWithAuth } from '../../utils/api'; // Token ile API çağrısı için

const ContactForm = () => {
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [gender, setGender] = useState('male');
  const [country, setCountry] = useState('');
  const [countries, setCountries] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    // Ülke listesini API'den al
    const fetchCountries = async () => {
      try {
        const data = await fetchWithAuth('/api/countries');
        setCountries(data);
      } catch (err) {
        setError('Ülke verilerini alırken bir hata oluştu.');
      }
    };

    fetchCountries();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (name.length > 50) {
      setError('İsim 50 karakterden uzun olamaz.');
      return;
    }

    if (message.length > 500) {
      setError('Mesaj 500 karakterden uzun olamaz.');
      return;
    }

    try {
      await fetchWithAuth('/api/message/add', {
        method: 'POST',
        body: JSON.stringify({ name, message, gender, country }),
        headers: { 'Content-Type': 'application/json' },
      });
      // Başarıyla gönderildiğinde yapılacak işlemler
      setName('');
      setMessage('');
      setGender('male');
      setCountry('');
      setError('');
      alert('Mesajınız başarıyla gönderildi!');
    } catch (err) {
      setError('Mesaj gönderilirken bir hata oluştu.');
    }
  };

  return (
    <div className="contact-form">
      <h2>İletişim Formu</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">İsim:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            maxLength="50"
            required
          />
        </div>
        <div>
          <label>Cinsiyet:</label>
          <div>
            <label>
              <input
                type="radio"
                name="gender"
                value="male"
                checked={gender === 'male'}
                onChange={() => setGender('male')}
              />
              Erkek
            </label>
            <label>
              <input
                type="radio"
                name="gender"
                value="female"
                checked={gender === 'female'}
                onChange={() => setGender('female')}
              />
              Kadın
            </label>
          </div>
        </div>
        <div>
          <label htmlFor="country">Ülke:</label>
          <select
            id="country"
            value={country}
            onChange={(e) => setCountry(e.target.value)}
            required
          >
            <option value="">Bir ülke seçin</option>
            {countries.map((c) => (
              <option key={c.code} value={c.name}>
                {c.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="message">Mesaj:</label>
          <textarea
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            maxLength="500"
            required
          />
        </div>
        <button type="submit">Gönder</button>
      </form>
    </div>
  );
};

export default ContactForm;
