import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

export default function Sidebar() {
  const [userRole, setUserRole] = useState("");
  const [userName, setUserName] = useState("");
  const [userImgBase64, setUserImgBase64] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    setUserRole(JSON.parse(localStorage.getItem("user"))?.role ?? "");
    setUserName(JSON.parse(localStorage.getItem("user"))?.username ?? "");
    setUserImgBase64(
      JSON.parse(localStorage.getItem("user"))?.base64Photo ?? ""
    );

    console.log(userImgBase64);
  }, [userRole, userName, userImgBase64]);

  const handleLogout = () => {
    const token = localStorage.getItem("token");

    axios
      .post("http://localhost:5165/api/user/logout", null, {
        headers: {
          token,
        },
      })
      .then((res) => {
        console.log(res);
        setUserRole("");
        localStorage.removeItem("user");
        navigate("/login");
      })
      .catch((err) => {
        console.error("Could not log out: ", err);
      });
  };

  return (
    <div>
      {(userRole === "admin" || userRole === "reader") && (
        <nav className="Sidebar Sidebar-expand-lg">
          <div className="container-fluid">
            <Link className="Sidebar-brand text-warning fw-bold" to="/" style={{ fontSize: "36px" }}>
              🫡 Welcome
            </Link>
            <button
              className="Sidebar-toggler navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#SidebarNav"
              aria-controls="SidebarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
              style={{ fontSize: "24px" }}
            >
              <img
                src={userImgBase64}
                alt="Profile Pic"
                style={{
                  width: '50px',
                  height: '50px',
                  borderRadius: '50%',
                  cursor: 'pointer'
                }}
              />
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse sidebar-collapse" id="SidebarNav">
              {userRole === "admin" && (
                <ul className="navbar-nav me-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to="/messages" style={{ fontSize: "24px" }}>
                      <span style={{ color: "#bbc1c9" }}>{"Messages"}</span>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/users" style={{ fontSize: "24px" }}>
                      <span style={{ color: "#bbc1c9" }}>{"Users"}</span>
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to="/reports" style={{ fontSize: "24px" }}>
                      <span style={{ color: "#bbc1c9" }}>{"Reports"}</span>
                    </Link>
                  </li>
                </ul>
              )}
              {userRole === "reader" && (
                <ul className="navbar-nav me-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to="/messages" style={{ color: "white", fontSize: "24px" }}>
                      <span style={{ color: "#bbc1c9" }}>{"Messages"}</span>
                    </Link>
                  </li>
                </ul>
              )}
              <ul className="Sidebar-nav mb-2 mb-lg-0">
                <li className="nav-item dropdown me-5">
                  <Link
                    className="nav-link dropdown-toggle"
                    role="button"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                    style={{ fontSize: "24px" }}
                  >
                    <img
                      src={userImgBase64}
                      alt="Profile Pic"
                      className="rounded-circle img-fluid"
                      style={{ width: "40px", height: "40px" }}
                    />
                  </Link>
                  <ul className="dropdown-menu">
                    <li className="text-center">
                      <span className="dropdown-item-text" style={{ fontSize: "24px" }}>{userName}</span>
                    </li>
                    <hr />
                    <li className="dropdown-item d-grid">
                      <button
                        type="button"
                        className="btn custom-logout-button"
                        style={{ backgroundColor: "#85898f", color: "#2f3133", border: "black", fontSize: "20px", padding: "15px" }}
                        onClick={handleLogout}
                      >
                        Logout
                      </button>
                    </li>
                  </ul>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      )}
      {!userRole && (
        <nav className="Sidebar Sidebar-expand-lg">
          <div className="container-fluid">
            <Link className="Sidebar-brand text-warning fw-bold" to="/" style={{ fontSize: "36px" }}>
              Home 🏠
            </Link>
            <div className="collapse Sidebar-collapse" id="SidebarNav">
              <button
                type="button"
                className="btn custom-logout-button"
                style={{
                  backgroundColor: "#85898f",
                  color: "#2f3133",
                  border: "black",
                  width: "200px", // Increased width
                  height: "60px", // Increased height
                  fontSize: "24px", // Increased font size
                  padding: "15px" // Increased padding
                }}
              >
                <Link
                  className="nav-link"
                  to="/login"
                  style={{ fontSize: "24px", fontWeight: "bold", color: "white" }}
                >
                  Login
                </Link>
              </button>
            </div>
            <button
              className="Sidebar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#SidebarNav"
              aria-controls="SidebarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
              style={{ fontSize: "30px" }}
            >
              <span
                className="Sidebar-toggler-icon"
                style={{
                  color: "#85898f", // Gray color
                  fontSize: "36px", // Increased size
                  backgroundColor: "#f0f0f0", // Light gray background
                  padding: "12px", // Padding
                  borderRadius: "12px" // Rounded corners
                }}
              >
                ☰
              </span>
            </button>
            <ul className="Sidebar-nav mb-2 mb-lg-0"></ul>
          </div>
        </nav>
      )}
    </div>
  );
  
}
