import { useState, useEffect } from 'react';
import axios from "axios";
import Sidebar from '../components/common/Sidebar';


export default function HomePage() {
  const [formData, setFormData] = useState({
    name: "",
    message: "",
    gender: "male",
    country: "",
  });

  const [countries, setCountries] = useState([]);
  const [error, setError] = useState(null);
  const [showSuccessAlert, setShowSuccessAlert] = useState(false);
  const [cardBgColor, setCardBgColor] = useState("rgba(0, 0, 0, 0.4)");

  useEffect(() => {
      setCardBgColor("#999894"); 
  }, [cardBgColor]);

  useEffect(() => {
    axios
      .get("http://localhost:5165/api/countries")
      .then((res) => {
        setCountries(res.data.data.countries);
      })
      .catch((err) => {
        console.error("Error while fetching the countries", err);
      });
  }, []);

  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };


  // Handle user login
  const handleLogin = (e) => {
    e.preventDefault();
    console.log("Form is being submitted:", formData);

    axios
      .post("http://localhost:5165/api/message/add", formData)
      .then((res) => {
        console.log("Form submitted succesffully:", res.data);
        setShowSuccessAlert(true);
      })
      .catch((err) => {
        console.error("Error sending form data:", err);
      setError(true);
      });
  };



    return (
      <div>
        <Sidebar />
        <div className="container mt-5 d-flex justify-content-center align-items-center">
          <div className="card w-50" style={{ backgroundColor: cardBgColor, padding: "4rem" }}>
            <div className="card-header" style={{ padding: "2rem" }}>
              <h2 className="card-title" style={{ fontSize: "48px" }}>
                {"Contact Us"}
              </h2>
            </div>
            <div className="card-body">
              {showSuccessAlert && (
                <div
                  className="alert alert-primary alert-dismissible fade show mt-4 mb-4"
                  role="alert"
                  style={{ fontSize: "22px", padding: "1.5rem" }}
                >
                  {"Form submitted successfully."}
                  <button
                    type="button"
                    className="btn-close"
                    data-bs-dismiss="alert"
                    aria-label="Close"
                    onClick={() => setShowSuccessAlert(false)}
                  ></button>
                </div>
              )}
    
              {error && (
                <div
                  className="alert alert-danger alert-dismissible fade show mt-4 mb-4"
                  role="alert"
                  style={{ fontSize: "22px", padding: "1.5rem" }}
                >
                  {"An error occurred while submitting the form."}
                  <button
                    type="button"
                    className="btn-close"
                    data-bs-dismiss="alert"
                    aria-label="Close"
                    onClick={() => setError(false)}
                  ></button>
                </div>
              )}
    
              <form onSubmit={handleLogin}>
                <div className="mb-4">
                  <label htmlFor="name" className="form-label" style={{ fontSize: "24px" }}>
                    {"Full Name"}
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    name="name"
                    maxLength="50"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    style={{ fontSize: "22px", padding: "20px" }} // Increased font size and padding
                  />
                </div>
                <div className="mb-4">
                  <label className="form-label" style={{ fontSize: "24px" }}>
                    {"Gender"}
                  </label>
                  <div className="form-check" style={{ fontSize: "22px" }}>
                    <input
                      type="radio"
                      className="form-check-input"
                      id="male"
                      name="gender"
                      value="male"
                      checked={formData.gender === "male"}
                      onChange={handleInputChange}
                      style={{ transform: "scale(1.5)" }} // Increased size of radio button
                    />
                    <label className="form-check-label" htmlFor="male">
                      {"Male"}
                    </label>
                  </div>
                  <div className="form-check" style={{ fontSize: "22px" }}>
                    <input
                      type="radio"
                      className="form-check-input"
                      id="female"
                      name="gender"
                      value="female"
                      checked={formData.gender === "female"}
                      onChange={handleInputChange}
                      style={{ transform: "scale(1.5)" }} // Increased size of radio button
                    />
                    <label className="form-check-label" htmlFor="female">
                      {"Female"}
                    </label>
                  </div>
                </div>
                <div className="mb-4">
                  <label htmlFor="country" className="form-label" style={{ fontSize: "24px" }}>
                    {"Country"}
                  </label>
                  <select
                    className="form-select"
                    id="country"
                    name="country"
                    value={formData.country}
                    onChange={handleInputChange}
                    required
                    style={{ fontSize: "22px", padding: "20px" }} // Increased font size and padding
                  >
                    <option value="" disabled>
                      {"Select a country"}
                    </option>
                    {countries.map((country, index) => (
                      <option key={index} value={country}>
                        {country}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="mb-4">
                  <label htmlFor="message" className="form-label" style={{ fontSize: "24px" }}>
                    {"Message"}
                  </label>
                  <textarea
                    className="form-control"
                    id="message"
                    name="message"
                    rows="7"
                    maxLength="500"
                    value={formData.message}
                    onChange={handleInputChange}
                    required
                    style={{ fontSize: "22px", padding: "20px" }} // Increased font size and padding
                  ></textarea>
                </div>
                <div className="d-grid">
                  <button type="submit" className="btn btn-outline-primary" style={{ fontSize: "24px", padding: "20px" }}>
                    {"Submit"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
    
}
