import  { createContext, useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { fetchWithAuth } from '../utils/api';
export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Kullanıcıyı backend'den kontrol edin veya token'ı decode edin
      setUser({ token }); // Bu örnek, token'ın varlığını kullanıcı varlığı olarak kabul eder
    }
  }, []);

  const login = async (userData) => {
    const { token } = await loginUser(userData.username, userData.password);
    setUser({ token });
    localStorage.setItem('token', token);
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

AuthProvider.propTypes = {
  children: PropTypes.node.isRequired,
};
