import { useState, useEffect } from 'react';
import { fetchWithAuth } from '../utils/api';
import LoginPage from './LoginPage';
import ContactForm from '../components/messages/ContactForm';
import '../assets/styles/global.css';

const HomePage = () => {
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const data = await fetchWithAuth('/api/countries');
        setCountries(data);
      } catch (err) {
        console.error('Ülkeler yüklenirken hata oluştu:', err);
      }
    };

    fetchCountries();
  }, []);

  return (
    <div className="home-page">
      <h1>Hoşgeldiniz!</h1>
      <div className="login-contact-container">
        <div className="login-section">
          <LoginPage />
        </div>
        <div className="contact-form-section">
          <ContactForm countries={countries} />
        </div>
      </div>
    </div>
  );
};

export default HomePage;
