import { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './LoginPage';
import ContactForm from '../components/messages/ContactForm';
//import Sidebar from '../components/common/Sidebar';
import UsersPage from './UserPage';
import ReportsPage from './ReportsPage';
import '../assets/styles/global.css';

const HomePage = () => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [user, setUser] = useState(null);

  //const navigate = useNavigate(); // useNavigate hook'unu kullanıyoruz

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch('http://localhost:5165/api/user');
        const result = await response.json();
        setUser(result.user);
      } catch (error) {
        console.error('Kullanıcı bilgileri alınırken bir hata oluştu:', error);
      }
    };

    fetchUser();
  }, []);

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch('http://localhost:5165/api/countries');
        const result = await response.json();
        setCountries(result.data.countries || []);
      } catch (error) {
        setError('Ülkeler yüklenirken bir hata oluştu.');
        console.error('Ülkeler yüklenirken bir hata oluştu:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCountries();
  }, []);

  /*const handleLogout = () => {
    // Burada çıkış yapma işlemini gerçekleştirin
    setUser(null);
    // Kullanıcıyı giriş sayfasına yönlendirin
    navigate('/');
  };
*/
  if (!user) {
    return (
      <div className="home-page">
        <h1>Hoşgeldiniz!</h1>
        <div className="login-contact-container">
          <div className="login-section">
            <LoginPage />
          </div>
          <div className="contact-form-section">
            {loading && <p>Ülkeler yükleniyor...</p>}
            {error && <p>{error}</p>}
            {!loading && !error && <ContactForm countries={countries} />}
          </div>
        </div>
      </div>
    );
  }
  

  return (
    <div className="home-page">

      <div className="content">
        <Routes>
          {user.role === 'admin' && (
            <>
              <Route path="/messages" element={<ContactForm countries={countries} />} />
              <Route path="/users" element={<UsersPage />} />
              <Route path="/reports" element={<ReportsPage />} />
            </>
          )}
          {user.role === 'reader' && (
            <Route path="/messages" element={<ContactForm countries={countries} />} />
          )}
          <Route path="*" element={<Navigate to="/messages" />} />
        </Routes>
      </div>
    </div>
  );
};

export default HomePage;
