import { useEffect, useState } from "react";
import Sidebar from '../components/common/Sidebar';
import axios from "axios";
import PieChart from "../components/reports/PieChart";
import BarChart from "../components/reports/BarChart";

export default function Reports() {
  const [messages, setMessages] = useState([]);
  const [genderDistribution, setGenderDistribution] = useState({ male: 0, female: 0 });
  const [countryMessageCounts, setCountryMessageCounts] = useState({});
  const [cardBackground, setCardBackground] = useState("rgba(0, 0, 0, 0.2)");

  useEffect(() => {
    // Set a different card background color
    setCardBackground("#999894");
  }, []);

  useEffect(() => {
    const token = localStorage.getItem("token");

    axios
      .get("http://localhost:5165/api/messages", { headers: { token } })
      .then((response) => {
        setMessages(response.data.data.messages);
        console.log("Messages fetched: ", response);
      })
      .catch((error) => {
        console.error("Error fetching messages: ", error);
      });
  }, []);

  useEffect(() => {
    if (messages.length > 0) {
      setGenderDistribution(calculateGenderDistribution(messages));
      setCountryMessageCounts(calculateCountryMessageCounts(messages));
    }
  }, [messages]);

  function calculateGenderDistribution(messages) {
    const genderCounts = { male: 0, female: 0 };

    messages.forEach(message => {
      if (message.gender === "male") {
        genderCounts.male++;
      } else if (message.gender === "female") {
        genderCounts.female++;
      }
    });

    return genderCounts;
  }

  function calculateCountryMessageCounts(messages) {
    const countryCounts = {};

    messages.forEach(message => {
      const { country } = message;

      if (countryCounts[country]) {
        countryCounts[country]++;
      } else {
        countryCounts[country] = 1;
      }
    });

    return countryCounts;
  }


  return (
    <div>
      < Sidebar/>
      <div className="container mt-4">
        <div className="card" style={{ backgroundColor: cardBgColor }}>
          <div className="card-header">
            <h3>Reports</h3>
          </div>
          <div className="card-body">
            <div className="row mb-2">
              <div className="col-4 d-flex align-items-center">
                <h3 className="card-title text-warning">
                 Message counts by countries:
                </h3>
              </div>
              <div className="col">
                <div>
                  <BarChart
                    id="country"
                    data={messageCountsByCountry}
                  />
                </div>
              </div>
            </div>
            <hr />
            <div className="row">
              <div className="col-4 d-flex align-items-center">
                <h3 className="card-title text-warning" >
                  Message counts by genders:
                </h3>
              </div>
              <div className="col">
                <div>
                  <PieChart
                    id="gender"
                    male={genderCounts.male}
                    female={genderCounts.female}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
