import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

export default function Sidebar() {
  const [userRole, setUserRole] = useState("");
  const [userName, setUserName] = useState("");
  const [userImgBase64, setUserImgBase64] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    setUserRole(JSON.parse(localStorage.getItem("user"))?.role ?? "");
    setUserName(JSON.parse(localStorage.getItem("user"))?.username ?? "");
    setUserImgBase64(
      JSON.parse(localStorage.getItem("user"))?.base64Photo ?? ""
    );

    console.log(userImgBase64);
  }, [userRole, userName, userImgBase64]);

  const handleLogout = () => {
    const token = localStorage.getItem("token");

    axios
      .post("http://localhost:5165/api/user/logout", null, {
        headers: {
          token,
        },
      })
      .then((res) => {
        console.log(res);
        setUserRole("");
        localStorage.removeItem("user");
        navigate("/login");
      })
      .catch((err) => {
        console.error("Could not log out: ", err);
      });
  };

  
  return (
    <div style={{ position: 'relative' }}>
      <nav
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          height: '100%',
          width: isSidebarOpen ? '250px' : '60px',
          backgroundColor: '#333',
          color: '#fff',
          transition: 'width 0.3s',
          overflowX: 'hidden',
          zIndex: 1000
        }}
      >
        <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
          <div style={{ padding: '10px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Link className="Sidebar-brand text-warning fw-bold" to="/" style={{ color: '#ffc107' }}>
              ðŸ«¡ Welcome
            </Link>
            <button
              type="button"
              onClick={toggleSidebar}
              aria-controls="SidebarNav"
              aria-expanded={isSidebarOpen}
              aria-label="Toggle navigation"
              style={{
                background: 'none',
                border: 'none',
                cursor: 'pointer',
                padding: 0
              }}
            >
              <img
                src={userImgBase64}
                alt="Profile Pic"
                style={{
                  width: '40px',
                  height: '40px',
                  borderRadius: '50%',
                  cursor: 'pointer'
                }}
              />
            </button>
          </div>
          <div
            id="SidebarNav"
            style={{
              display: isSidebarOpen ? 'block' : 'none',
              overflowY: 'auto',
              flex: 1
            }}
          >
            {userRole === "admin" && (
              <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
                <li style={{ padding: '10px' }}>
                  <Link className="nav-link" to="/messages" style={{ color: '#bbc1c9' }}>
                    <h7>Messages</h7>
                  </Link>
                </li>
                <li style={{ padding: '10px' }}>
                  <Link className="nav-link" to="/users" style={{ color: '#bbc1c9' }}>
                    <h7>Users</h7>
                  </Link>
                </li>
                <li style={{ padding: '10px' }}>
                  <Link className="nav-link" to="/reports" style={{ color: '#bbc1c9' }}>
                    <h7>Reports</h7>
                  </Link>
                </li>
              </ul>
            )}
            {userRole === "reader" && (
              <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
                <li style={{ padding: '10px' }}>
                  <Link className="nav-link" to="/messages" style={{ color: 'white' }}>
                    <h7 style={{ color: 'black' }}>Messages</h7>
                  </Link>
                </li>
              </ul>
            )}
            <ul style={{ listStyle: 'none', padding: 0, margin: '20px 0' }}>
              <li className="nav-item dropdown me-5" style={{ padding: '10px' }}>
                <div className="dropdown-menu">
                  <li className="text-center">
                    <span className="dropdown-item-text">{userName}</span>
                  </li>
                  <hr />
                  <li className="dropdown-item d-grid">
                    <button
                      type="button"
                      className="btn"
                      style={{
                        backgroundColor: '#85898f',
                        color: '#2f3133',
                        border: '1px solid black',
                        width: '100%',
                        padding: '10px'
                      }}
                      onClick={handleLogout}
                    >
                      Logout
                    </button>
                  </li>
                </div>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
}
