import { useState, useEffect, useContext } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import LoginPage from './LoginPage';
import ContactForm from '../components/messages/ContactForm';
import UsersPage from './UserPage';
import ReportsPage from './ReportsPage';
import Sidebar from '../components/common/Sidebar';
import '../assets/styles/global.css';
import { AuthContext } from '../context/AuthContext';

const HomePage = () => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user, login } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch('http://localhost:5165/api/countries');
        const result = await response.json();
        setCountries(result.data.countries || []);
      } catch (error) {
        setError('Ülkeler yüklenirken bir hata oluştu.');
        console.error('Ülkeler yüklenirken bir hata oluştu:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCountries();
  }, []);

  const handleLogin = async (userData) => {
    try {
      await login(userData);
      navigate('/');
    } catch (error) {
      console.error('Giriş yaparken bir hata oluştu:', error);
    }
  };

  if (!user) {
    return (
      <div className="home-page">
        <h1>Hoşgeldiniz!</h1>
        <div className="login-contact-container">
          <div className="login-section">
            <h2>Admin Girişi</h2>
            <LoginPage onLogin={(userData) => handleLogin({ ...userData, role: 'admin' })} />
          </div>
          <div className="login-section">
            <h2>Reader Girişi</h2>
            <LoginPage onLogin={(userData) => handleLogin({ ...userData, role: 'reader' })} />
          </div>
          <div className="contact-form-section">
            {loading && <p>Ülkeler yükleniyor...</p>}
            {error && <p>{error}</p>}
            {!loading && !error && <ContactForm countries={countries} />}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="home-page">
      <Sidebar role={user.role} onLogout={handleLogout} />
      <div className="content">
        <Routes>
          {user.role === 'admin' && (
            <>
              <Route path="/messages" element={<ContactForm countries={countries} />} />
              <Route path="/users" element={<UsersPage />} />
              <Route path="/reports" element={<ReportsPage />} />
            </>
          )}
          {user.role === 'reader' && (
            <Route path="/messages" element={<ContactForm countries={countries} />} />
          )}
          <Route path="*" element={<Navigate to="/messages" />} />
        </Routes>
      </div>
    </div>
  );
};

export default HomePage;
