import  { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

const Sidebar = () => {
  const { user } = useContext(AuthContext); // Kullanıcı bilgisini AuthContext'ten al

  if (!user) return null; // Kullanıcı yoksa menüyü göstermeyin

  const isAdmin = user.role === 'admin'; // Kullanıcı rolünü kontrol et

  return (
    <div className="sidebar">
      <h2>Menü</h2>
      <ul>
        {isAdmin && (
          <>
            <li><Link to="/messages">Mesajlar</Link></li>
            <li><Link to="/users">Kullanıcılar</Link></li>
            <li><Link to="/reports">Raporlar</Link></li>
          </>
        )}
        {!isAdmin && (
          <li><Link to="/messages">Mesajlar</Link></li>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
