  useEffect(() => {
    fetchMessages();
  }, [currentPage]);

  return (
    <div className={darkMode ? "bg-dark text-light" : ""}>
      <div>
        <Sidebar />
        <div className="container mt-2">
          <div
            className="card"
            style={{
              backgroundColor: outerCardBgColor,
            }}
          >
            <div className="card-header">
              <h3>{t("messages")}</h3>
            </div>
            <div className="card-body">
              <div className="row mb-4 d-flex align-items-center">
                <div className="col-">
                  <div className="form-group">
                    <label htmlFor="sortBy">
                      {t("sortBy")}
                    </label>
                    <select
                      id="sortBy"
                      className="form-control"
                      value={sortBy}
                      onChange={(e) => setSortBy(e.target.value)}
                    >
                      <option value="date_desc">
                        {t("dateDescending")}
                      </option>
                      <option value="date_asc">
                        {t("dateAscending")}
                      </option>
                      <option value="sender_name_asc">
                        {t("senderNameAscending")}
                      </option>
                      <option value="sender_name_desc">
                        {t("senderNameDescending")}
                      </option>
                    </select>
                  </div>
                </div>
                <div className="col-4">
                  <div className="form-group">
                    <label>
                      {t("filterBy")}
                    </label>
                    <div>
                      <div className="form-check form-check-inline">
                        <input
                          type="checkbox"
                          id="male"
                          value="male"
                          checked={filterOptions.gender.includes("male")}
                          onChange={() => handleGenderFilterChange("male")}
                          className="form-check-input"
                        />
                        <label htmlFor="male" className="form-check-label">
                          {t("male")}
                        </label>
                      </div>
                      <div className="form-check form-check-inline">
                        <input
                          type="checkbox"
                          id="female"
                          value="female"
                          checked={filterOptions.gender.includes("female")}
                          onChange={() => handleGenderFilterChange("female")}
                          className="form-check-input"
                        />
                        <label htmlFor="female" className="form-check-label">
                          {t("female")}
                        </label>
                      </div>
                      <div className="form-check form-check-inline">
                        <input
                          type="checkbox"
                          id="read"
                          value="read"
                          checked={filterOptions.status.includes("read")}
                          onChange={() => handleReadStatusFilterChange("read")}
                          className="form-check-input"
                        />
                        <label htmlFor="read" className="form-check-label">
                          {t("read")}
                        </label>
                      </div>
                      <div className="form-check form-check-inline">
                        <input
                          type="checkbox"
                          id="unread"
                          value="unread"
                          checked={filterOptions.status.includes("unread")}
                          onChange={() => handleReadStatusFilterChange("unread")}
                          className="form-check-input"
                        />
                        <label htmlFor="unread" className="form-check-label">
                          {t("unread")}
                        </label>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <div
                className="table-responsive card"
                style={{
                  backgroundColor: innerCardBgColor,

                }}
              >
                <table
                  className={`table table-hover table- table-borderless`}
                >
                  <thead>
                    <tr>
                      <th>{t("status")}</th>
                      <th>{t("name")}</th>
                      <th>{t("message")}</th>
                      <th></th>
                    </tr>
                  </thead>
                  <tbody>
                    {messages.map((message) => (
                      <tr
                        key={message.id}
                        className="clickable-row"
                        onClick={() => {
                          navigateToMessageDetails(message.id);
                        }}
                      >
                        <td
                          className={
                            message.read == "true" ? "text-secondary" : "text-warning"}
                        >
                          {message.read == "true" ? t("read") : t("unread")}
                        </td>
                        <td
                          className={
                            message.read == "true" ? "text-secondary" : ""
                          }
                        >
                          {message.name}
                        </td>
                        <td
                          className={
                            message.read == "true" ? "text-secondary" : ""
                          }
                        >
                          {message.message}
                        </td>
                        <td
                          className={
                            message.read == "true" ? "text-secondary" : ""
                          }
                        >
                          {userRole === "admin" && (
                            <span
                              className="material-symbols-outlined delete-icon"
                              onClick={(event) => {
                                event.stopPropagation();
                                setMessageIdToBeDeleted(message.id);
                                openModal();
                              }}
                            >
                              {t("delete")}
                            </span>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>

                  
                </table>
              </div>

              <div className="pagination-controls">
                <button
                  disabled={currentPage === 1} // Eğer ilk sayfadaysa geri butonunu devre dışı bırak
                  onClick={() => setCurrentPage((prevPage) => Math.max(prevPage - 1, 1))}
                >
                  {t("prevPage")}
                </button>
                <span>
                  {t("page")} {currentPage} {t("of")} {totalPages}
                </span>
                <button
                  disabled={currentPage === totalPages} // Eğer son sayfadaysa ileri butonunu devre dışı bırak
                  onClick={() =>
                    setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages))
                  }
                >
                  {t("nextPage")}
                </button>
              </div>

            </div>
          </div>
        </div>
        <Modal show={showModal} onHide={closeModal}>
          <Modal.Header closeButton>
            <Modal.Title>
              {t("confirmDelete")}
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {t("deleteMessageConfirm")}
            {messageDeleteErrorAlert && (
              <div className="alert alert-danger">
                {t("deleteError")}
              </div>
            )}
          </Modal.Body>
          <Modal.Footer>
            <button className="btn btn-secondary" onClick={closeModal}>
              {t("close")}
            </button>
            <button className="btn btn-danger" onClick={handleDeleteMessage}>
              {t("delete")}
            </button>
          </Modal.Footer>
        </Modal>
      </div>
    </div>

  );
}
